import React, {Component} from 'react';
import {Button, Form, Modal, NavLink} from 'react-bootstrap';
import { connect } from 'react-redux';

class Modalauth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formType: this.props.btnName,
    }
  }

  hiddenClick = (e) => {
    // e.preventDefault();
  };

  hiddenSubmit = (e) => {
    e.preventDefault();
    console.log(e);
  }

  loginOrlogout = () => {
    if (this.state.formType == "login") {
      return (
        <Form onSubmit={this.hiddenSubmit}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicCreateAccountLink">
            <Form.Label>
              <Button onClick={this.setState({formType: "register"})}>create an account</Button>
            </Form.Label>
          </Form.Group>

          <Button variant="primary" type="submit" onClick={this.hiddenClick}>
            Submit
          </Button>
        </Form>
      );
    } else if (this.state.formType == "register") {
      return (
        <Form onSubmit={this.hiddenSubmit}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicConfirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control type="password" placeholder="Confirm Password"/>
          </Form.Group>

          <Button variant="primary" type="submit" onClick={this.hiddenClick}>
            Submit
          </Button>
        </Form>
      );
    }
  };

  render() {
    console.log(this.state.formType);
    return (
      <>
        <Modal
          {...this.props}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header>
            <Modal.Title id="contained-modal-title-vcenter">
              Authorizate
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {this.loginOrlogout()}
          </Modal.Body>
        </Modal>
      </>
    )
  }
}

export default connect(
  (state) => ({
    storeUsers: state.users,
  }),
  (dispatch) => ({
    onAddUsers: (addUsers) => {
      dispatch({ type: 'ADD_USERS', payload: addUsers });
    },
  })
)(Modalauth);
