import React, {Component} from 'react';
import {Button, Form, Modal, Alert} from 'react-bootstrap';
import { connect } from 'react-redux';

class Modalauth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      formType: this.props.btnName,
    }
  }

  hiddenClick = (e) => {
    // e.preventDefault();
  };



  loginOrlogout = () => {
    if (this.state.formType === "login") {
      const loginHiddenSubmit = (e) => {
        e.preventDefault();
        const email = e.target.formBasicEmail.value;
        const password = e.target.formBasicPassword.value;

        this.props.storeUsers.map((item) => {
          if (email !== "" && password !== "" && email === item.email && password === item.password) {
            console.log("good auth");
            this.props.onHide();
          } else {
            return (
              <Alert variant="danger">Неверная почта или пароль</Alert>
            )
          }
        })
      }

      return (
        <Form onSubmit={loginHiddenSubmit}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Почта</Form.Label>
            <Form.Control type="email" placeholder="Введите почту"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Пароль</Form.Label>
            <Form.Control type="password" placeholder="Введите пароль"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicCreateAccountLink">
            <Form.Label>
              <Button variant="link" onClick={() => {this.setState({formType: "register"})}}>Нажмите чтобы создать аккаунт</Button>
            </Form.Label>
          </Form.Group>

          <Button variant="primary" type="submit" onClick={this.hiddenClick}>
            Сохранить
          </Button>
        </Form>
      );
    } else if (this.state.formType === "register") {
      const registerHiddenSubmit = (e) => {
        e.preventDefault();
        const email = e.target.formBasicEmail.value;
        const password = e.target.formBasicPassword.value;
        const confirmPassword = e.target.formBasicConfirmPassword.value;

        if (email !== "" && password !== "" && confirmPassword !== "" && password === confirmPassword) {
          const createUsers = {
            email: email,
            password: password,
          }
          this.props.onAddUsers(createUsers);
          this.props.onHide();
        } else {
          return (
            <Alert variant="danger">Не все данные введены</Alert>
          )
        }
      }

      return (
        <Form onSubmit={registerHiddenSubmit}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Почта</Form.Label>
            <Form.Control type="email" placeholder="Введите почту"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Пароль</Form.Label>
            <Form.Control type="password" placeholder="Введите пароль"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicConfirmPassword">
            <Form.Label>Подтвердите пароль</Form.Label>
            <Form.Control type="password" placeholder="Подтвердите пароль"/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicCreateAccountLink">
            <Form.Label>
              <Button variant="link" onClick={() => {this.setState({formType: "login"})}}>Нажмите если есть аккаунт</Button>
            </Form.Label>
          </Form.Group>

          <Button variant="primary" type="submit" onClick={this.hiddenClick}>
            Сохранить
          </Button>
        </Form>
      );
    }
  };

  render() {
    return (
      <>
        <Modal
          {...this.props}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header>
            <Modal.Title id="contained-modal-title-vcenter">
              Авторизация
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {this.loginOrlogout()}
          </Modal.Body>
        </Modal>
      </>
    )
  }
}

export default connect(
  (state) => ({
    storeUsers: state.users,
  }),
  (dispatch) => ({
    onAddUsers: (addUsers) => {
      dispatch({ type: 'ADD_USERS', payload: addUsers });
    },
  })
)(Modalauth);
